create database if not exists teamsphere;

drop table if exists followers;
drop table if exists likes;
drop table if exists sales;
drop table if exists products;
drop table if exists users;
drop table if exists posts;


create table if not exists posts(
	idPost varchar(255) primary key,
    title varchar(100) not null,
    description varchar(800) not null,
    type varchar(20) not null,
    image varchar(2000)
);

create table if not exists users(
	idUser varchar(255) primary key,
    name varchar(50) not null,
    last_name varchar(150) not null,
    email varchar(50) not null,
    phone varchar(500),
    icon varchar(2000) not null,
    password varchar(50) not null,
    role enum("Administrador","Usuario") not null,
    fk_idPost varchar(255),
    foreign key (fk_idPost) references posts(idPost)
);

create table if not exists products(
	idProduct varchar(255) primary key,
    name varchar(100) not null,
    description varchar(500) not null,
    price double not null,
    image varchar(2000) not null,
    date datetime not null,
    fk_idBuyer varchar(255),
    foreign key (fk_idBuyer) references users(idUser),
    fk_idSeller varchar(255) not null,
    foreign key (fk_idSeller) references users(idUser)

);

create table if not exists sales(
	idUserProduct varchar(255) primary key,
    fk_idUser varchar(255) not null,
    foreign key (fk_idUser) references users(idUser),
    fk_idProduct varchar(255) not null,
    foreign key (fk_idProduct) references products(idProduct)

);

create table if not exists followers (
	idFollowerFollowed varchar(255) primary key,
    fk_idFollower varchar(255) not null,
    foreign key (fk_idFollower) references users(idUser),
    fk_idFollowed varchar(255) not null,
    foreign key (fk_idFollowed) references users(idUser)
);

create table if not exists likes (
	idLike varchar(255) primary key,
    fk_idUser varchar(255) not null,
    foreign key (fk_idUser) references users(idUser),
    fk_idPost varchar(255) not null,
    foreign key (fk_idPost) references posts(idPost)
);
